mapper.select:
 	    SELECT *
		FROM (
		        SELECT rownum rn ,  a.*
		        FROM  (select * from emp order by empno) a
		     )
		WHERE rn between #{start} AND #{end}

 	     
 	    SELECT *
		FROM (
		        SELECT rownum rn ,  a.*
		        FROM  (select * from emp order by empno) a
		     )
		WHERE rn >= #{start} 
		AND rn <= #{end}

mapper.update:
update emp set ename=#{ename}, job=#{job}, mgr=#{mgr},
			   sal=#{sal},
			   hiredate=#{hiredate},
	 comm=#{comm}, 	
			  deptno = #{deptno}
where empno = #{empno}
